function name_airline_company(a)
{
  document.getElementById('name_airline_company').value=document.getElementById('name_airline_company'+a).value;
}
function check(a)
{
  for(i=1;i<=a;i++)
  {
    if(document.getElementById('check'+i).checked)
  {
    
    if(document.getElementById('name_airline_company').value=="")
    {
      document.getElementById('name_airline_company').value=document.getElementById('name_airline_company').value+document.getElementById('check'+i).value+"%";
    }
    else
    {
      document.getElementById('name_airline_company').value=document.getElementById('name_airline_company').value+"' or name_airline_company like '%"+document.getElementById('check'+i).value+"%";
    }
  }
  }
  
  
}
